import React, { useState, useEffect, useRef } from 'react';
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';
import '../styles/SpeechToText.css'; // Adjust the path as necessary

const SpeechToText = () => {
  const [text, setText] = useState('');
  const [words, setWords] = useState([]);
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const utteranceRef = useRef(null);
  const { transcript, resetTranscript, listening } = useSpeechRecognition();
  const timerRef = useRef(null);

  useEffect(() => {
    setText(transcript);
    if (listening) {
      clearTimeout(timerRef.current);
      timerRef.current = setTimeout(() => {
        handleSubmit(transcript);
      }, 5000);
    }
  }, [transcript, listening]);

  useEffect(() => {
    setWords(text.trim().split(/\s+/));
  }, [text]);

  const speakFromIndex = (index) => {
    if (index >= words.length) return;
    const utterance = new SpeechSynthesisUtterance(words.slice(index).join(' '));
    utterance.onboundary = (event) => {
      if (event.name === 'word') {
        const spokenText = utterance.text.substring(0, event.charIndex);
        const spokenWords = spokenText.trim().split(/\s+/).length;
        setCurrentWordIndex(index + spokenWords);
      }
    };
    utterance.onend = () => {
      setIsSpeaking(false);
      setIsPaused(false);
    };
    utteranceRef.current = utterance;
    speechSynthesis.speak(utterance);
    setIsSpeaking(true);
    setIsPaused(false);
  };

  console.log("words", words);
  console.log("currentWordIndex", currentWordIndex);

  const handlePlay = () => {
    if (isPaused) {
      speechSynthesis.resume();
      setIsPaused(false);
    } else {
      speechSynthesis.cancel();
      speakFromIndex(currentWordIndex);
    }
  };

  const handlePause = () => {
    speechSynthesis.pause();
    setIsPaused(true);
  };

  const handleStop = () => {
    speechSynthesis.cancel();
    setIsSpeaking(false);
    setIsPaused(false);
    setCurrentWordIndex(0);
  };

  const handleForward = () => {
    if (currentWordIndex < words.length - 1) {
      speechSynthesis.cancel();
      setCurrentWordIndex((prev) => prev + 1);
      speakFromIndex(currentWordIndex + 1);
    }
  };

  const handleRewind = () => {
    if (currentWordIndex > 0) {
      speechSynthesis.cancel();
      setCurrentWordIndex((prev) => prev - 1);
      speakFromIndex(currentWordIndex - 1);
    }
  };

  const handleMicClick = () => {
    if (!listening) {
      SpeechRecognition.startListening({ continuous: true });
    }
  };

  const handleStopClick = () => {
    if (listening) {
      SpeechRecognition.stopListening();
      resetTranscript();
    }
  };

  const handleResetClick = () => {
    setText('');
    resetTranscript();
    setCurrentWordIndex(0);
  };

  const handleInputChange = (e) => {
    setText(e.target.value);
    setCurrentWordIndex(0);
  };

  const handleSubmit = (finalTranscript) => {
    console.log('Submitted successfully:', finalTranscript);
    SpeechRecognition.stopListening();
  };

  return (
    <div className="speech-to-text">
      <div className="styled-input">
        {words.map((word, index) => (
          <span
            key={index}
            className={index === currentWordIndex ? 'highlight' : ''}
          >
            {word + ' '}
          </span>
        ))}
      </div>

      <div className="button-container">
        <button className="mic-button" onClick={handleMicClick}>üé§</button>
        <button className="stop-button" onClick={handleStopClick}>üõë</button>
        <button className="reset-button" onClick={handleResetClick}>üîÑ</button>
        <button className="submit-button" onClick={() => handleSubmit(text)}>Submit</button>
      </div>

      <div className="button-container">
        <button className="tts-button" onClick={handlePlay}>‚ñ∂Ô∏è Play</button>
        <button className="tts-button" onClick={handlePause}>‚è∏Ô∏è Pause</button>
        <button className="tts-button" onClick={handleStop}>‚èπÔ∏è Stop</button>
        <button className="tts-button" onClick={handleRewind}>‚è™ Rewind</button>
        <button className="tts-button" onClick={handleForward}>‚è© Forward</button>
      </div>
    </div>
  );
};

export default SpeechToText;
